namespace B2W\SkyHub\Model\Transformer\Order\Item;

use B2W\SkyHub\Model\Entity\Order\ItemEntity;
use B2W\SkyHub\Model\Transformer\DbToEntityAbstract;

/**
 * Class DbToEntity
 * @package B2W\SkyHub\Model\Transformer\Order\Item
 */
class DbToEntity extends DbToEntityAbstract
{
    /**
     * @return array|mixed
     */
    protected function _getAttributeMap()
    {
        return array();
    }

    /**
     * @return null
     * @throws \B2W\SkyHub\Exception\Data\RepositoryNotFound
     */
    public function convert()
    {
        return \App::repository(\App::REPOSITORY_ORDER_ITEM)->load($this->_post);
    }

    /**
     * @return ItemEntity|null
     */
    protected function _getEntityInstance()
    {
        return new ItemEntity();
    }
}
