namespace B2W\SkyHub\View;


/**
 * Class Template
 * @package B2W\SkyHub\View
 */
class Template
{
    /**
     * @var null
     */
    protected $_template = null;

    /**
     * @param $template
     * @return $this
     */
    public function setTemplate($template)
    {
        $this->_template = $template;
        return $this;
    }

    /**
     * @return $this|string
     */
    public function render()
    {
        if (empty($this->_template)) {
            return '';
        }

        $html = '';
        $file = \App::getBaseDir() . DS . 'view' . DS . $this->_template;

        if (file_exists($file)) {

            ob_start();
            include $file;

            $html = ob_get_clean();
        }

        echo $html;

        return $this;
    }
}
